insert  into `blogs_smart_article`(`oid`,`articleId`,`title`,`content`,`articleRandomDouble`) values (115,'7LfG2D1HcmA','北外与腾讯云签署合作协议，助推教师队伍建设','2019 年 3 月 21 日，北京外国语大学与\n<div class=\"entityContainer___2qvPy\" id=\"product7wEei0jEi0a\" data-reactroot=\"\" data-reactid=\"1\" data-react-checksum=\"-413395188\">\n <a class=\"entity___AMUDX\" href=\"https://search.readhub.cn/result?search=entity%2C7wEei0jEi0a%2Cproduct%2C%E8%85%BE%E8%AE%AF%E4%BA%91%2C%E8%85%BE%E8%AE%AF%E4%BA%91\" target=\"_blank\" data-reactid=\"2\">腾讯云</a>\n <div class=\"subBtn___fGrEA subBtn___3YUsQ\" data-reactid=\"3\">\n  <i class=\"iconPlus___ExbLU\" data-reactid=\"4\"></i>\n  <span class=\"content___1UZDd\" data-reactid=\"5\">订阅</span>\n </div>\n</div>在北京外国语大学东校区举行合作签约仪式，未来双方将共同做好人工智能助推教师队伍建设行动试点工作，积极探索新时代教师队伍改革新路径和教师队伍建设新方法 ... 在当今数字化教育的时代，北京外国语大学积极探索人工智能技术与教育融合的新路径，全面做好教育部人工智能助推教师队伍建设行动试点工作 ... 本次与腾讯云合作，双方就开展智能教室建设、智能化教师发展实验室建设和教师大数据建设等行动展开深度合作。\n<span id=\"detail-instantview\"></span>',1187);
