insert  into `blogs_smart_article`(`oid`,`articleId`,`title`,`content`,`articleRandomDouble`) values (166,'7LzjPxKZtfa','传软银从高盛等公司挖走 10 人以扩大其投资业务','据知情人士透露，\n<div class=\"entityContainer___2qvPy\" id=\"company7wEdfDxZOec\" data-reactroot=\"\" data-reactid=\"1\" data-react-checksum=\"154459240\">\n <a class=\"entity___AMUDX\" href=\"https://search.readhub.cn/result?search=entity%2C7wEdfDxZOec%2Ccompany%2C%E8%BD%AF%E9%93%B6%2C%E8%BD%AF%E9%93%B6\" target=\"_blank\" data-reactid=\"2\">软银</a>\n <div class=\"subBtn___fGrEA subBtn___3YUsQ\" data-reactid=\"3\">\n  <i class=\"iconPlus___ExbLU\" data-reactid=\"4\"></i>\n  <span class=\"content___1UZDd\" data-reactid=\"5\">订阅</span>\n </div>\n</div>集团计划从高盛等公司雇用大约 10 名新员工来提升其内部投资能力&nbsp;...&nbsp;知情人士称，这些新员工将来自银行业、私募股权、咨询业和医疗行业。他们说，他们将于 4 月和 5 月入职，分别在东京和伦敦工作&nbsp;...&nbsp;软银于 2018 年 6 月聘请了高盛前副总裁卡捷诺里·萨古担任首席战略官。在高盛工作了 18 年的资深员工 Hiroki Kimoto 在今年 1 月加入软银，负责领导一个新成立的投资部门。\n<span id=\"detail-instantview\"></span>',3932);
