insert  into `blogs_smart_article`(`oid`,`articleId`,`title`,`content`,`articleRandomDouble`) values (211,'7LBm4bZ5y3s','消息称紫辉创投 CEO 郑刚骗婚女演员，回应：被她倒打一耙','4 月 4 日下午消息，据百度《高光》栏目爆料，《新红楼梦》李纨饰演者周美毅遭紫辉创投 CEO 郑刚骗婚，郑刚联合情人抢走周美毅生下的双胞胎儿子，还在周美毅母亲葬礼上提出办离婚 ... 据《高光》栏目爆料，郑刚与周美毅 2016 年相识，半年后怀孕结婚，婚前郑刚声称有一「代孕」所生的女儿，并与周美毅签署了婚前财产协议 ... 结婚登记后一个月周美毅发现郑刚情人曹莉莉的存在，曹莉莉数次谩骂周美毅抢了自己老公，公婆哄骗周美毅称曹莉莉就是郑刚女儿的「代孕妈妈」。\n<span id=\"detail-instantview\"></span>',5203);
